import styled from "styled-components";

export const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;

    gap: 20px;

    width: 100%;

    margin-top: 100px;

    padding: 20px;

    background-color: rgba(0,0,0,0.5);

    color: ${({theme}) => theme.fontColor};

    & div {
        display: flex;
        flex-direction: column;
        align-items: start;
        justify-content: center;

        gap: 5px;
    }
`;

export const Header = styled.h1`
    font-size: 3rem;
    margin: 20px 0 50px;

    color: ${({theme}) => theme.fontColor};
`;

export const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: start;
    justify-content: center;

    gap: 20px;

`;

export const Label = styled.label`
    font-size: 1.5rem;
`;

export const MessageBox = styled.textarea`
    padding: 10px;

    width: 650px;
    min-height: 100px;

    border-radius: 3px;
    border: none;

    outline: none;

    font-size: 1.2rem;

    margin-bottom: 40px;

    resize: none;
`;

export const Input = styled.input`
    padding: 15px;

    width: 650px;

    border-radius: 5px;
    border: none;

    outline: none;

    font-size: 1.2rem;

`;


export const SendBtn = styled.input.attrs({
    type: 'submit'
  })`
  padding: 12px;
  width: 120px;



  color: ${({theme}) => theme.fontColor};

  border: none;

  background-color: ${({theme}) => theme.backgroundColor};
  box-shadow: 5px 5px 5px rgba(0,0,0,0.4);

  border-radius: 5px 5px 0 0;

  cursor: pointer;

  &:hover { 
    background-color: ${({theme}) => theme.phonebackgroundColor};
    color: yellow;

  }
  `;
