import styled from "styled-components";

export const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;

    gap: 20px;

    padding: 10px;

    margin-top: 200px;

    box-shadow: 5px 5px 5px rgba(0,0,0,0.4);
`;

export const Header = styled.div`
    margin-bottom: 50px;
    font-size: 3rem;


    color: ${({theme}) => theme.landingFontColor};
`;

export const JavaScript = styled.img`
   width: 700px;
    height: 500px;
    margin-right: 100px;


    box-shadow: 5px 5px 5px rgba(0,0,0,0.4);
`;

export const Designer = styled.img`
    width: 700px;
    height: 500px;


    box-shadow: 5px 5px 5px rgba(0,0,0,0.4);
`;

export const CyberSecurity = styled.img`
     width: 700px;
    height: 500px;


    margin-right: 100px;


    box-shadow: 5px 5px 5px rgba(0,0,0,0.4);
`;

export const EthicalHacker = styled.img`
