import { useRef, useState } from "react";
import { Container, Viewer, Image, ZoomBtns, ZoomIn, ZoomOut } from "./CertificateStyles";

function Certificate({ imageUrl }) {
    const [showViewer, setShowViewer] = useState(false);
    const imageRef = useRef("");
    const [currentImageWidth, setCurrentImageWidth] = useState(1);

    const onZoomIn = ()=> {
        if(!imageRef) return;
        imageRef.current.style.transform = `scale(${currentImageWidth + 0.5})`;
        setCurrentImageWidth(currentImageWidth + 0.5);
    }

    const onZoomOut = ()=> {
        if(!imageRef) return;
        if(currentImageWidth <= 1) return;
        imageRef.current.style.transform = `scale(${currentImageWidth - 0.5})`;
        setCurrentImageWidth(currentImageWidth - 0.5);
    }

    const Exit = ({target})=> {
        if(target.tagName !== "DIV") return;
        setShowViewer(false);
    }

    return <>
        <Image src={imageUrl} alt="certificate" onClick={() => setShowViewer(true)} />
        {showViewer && <Viewer>
            <Container  onClick={Exit}>
                <Image src={imageUrl} ref={imageRef}  alt="certificate" />
                <ZoomBtns>
                    <ZoomIn onClick={onZoomIn}>+</ZoomIn>
                    <ZoomOut onClick={onZoomOut}>-</ZoomOut>
                </ZoomBtns>
            </Container>
        </Viewer>}
    </>
}

export default Certificate;
