import styled, {keyframes} from "styled-components";


const appear = keyframes`
    from {
        transform: scale(0.4);
    }to {
        transform: scale(1);
    }

`


export const Image = styled.img`
    width: 600px;

    transition: all ease-in-out 300ms;
    cursor: pointer;
    &:hover {
        transform: scale(1.02);
    }

    @media(width <= 768px) {
        width: 350px;
    }
`;

export const Container = styled.div`
    width: 80%;
    height: 80vh;

    text-align: center;

    & ${Image} {
        width: unset;

        &:hover {
            transform: none;
            cursor: inherit;
        }
    }

    animation: ${appear} 0.3s linear;

    @media(width <= 768px) {
        width: 10%;
        height: 10vh;
    }


`;

export const Viewer = styled.div`
    position: fixed;
    top: 0;
    left: 0;

    background-color: rgba(0,0,0,0.7);

    width: 100%;
    height: 100vh;

    display: flex;
    justify-content: center;
    align-items: center;

`;
