import { createGlobalStyle } from "styled-components";

const GlobalStyles = createGlobalStyle`

@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:ital@0;1&family=Playfair:ital,opsz@0,5..1200;1,5..1200&display=swap');
    body {
        margin: 0;
        padding: 0;

        box-sizing: border-box;

        background: #1D2B36;

        color: #333333;


    }
`;

export default GlobalStyles;
