import styled from "styled-components";

export const Category = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;

    margin-bottom: 25px;

    gap: 30px;
`;

export const Image = styled.img`
    width: 50px;
    height: 50px;
`;

export const Area = styled.h1`
    font-size: 1.5rem;
`; 

export const Content = styled.p`
    font-size: 1rem;

    text-align: center;
`;
