import { useState } from "react";
import { Container, Viewer, Image, ZoomBtns } from "./CertificateStyles";

function Certificate({ imageUrl }) {
    const [showViewer, setShowViewer] = useState(false);
    return <>
        <Image src={imageUrl} alt="certificate" onClick={()=> setShowViewer(true)} />
        {showViewer && <Viewer onClick={()=> setShowViewer(false)}>
            <Container>
                <Image src={imageUrl} alt="certificate" />
            </Container>
        </Viewer>}
        <ZoomBtns>
            <ZoomIn>+</ZoomIn>
            <ZoomOut>-</ZoomOut>
        </ZoomBtns>
    </>
}

export default Certificate;
