import { Navbar, Logo, ThemeToggle, UList, ListElement, Menu, HamburgerMenu } from "./NavStyles";
import ThemeLogo from "./themes.png";
import DarkMenu from "./darkMenu.png";
import CloseWindow from "./closeWindow.png";
import { useState, useRef } from "react";
import { Link } from "react-router-dom";
import useGetWindowSize from "./useGetWindoSize";
import useGetScrollY from "./useGetScrollY";

function Nav({ changeTheme, currentThemeLogo }) {
  const [isOpen, setIsOpen] = useState(false);
  const [showMenu, setShowMenu] = useState(false);
  const screenSize = useGetWindowSize();
  const scrollYValue = useGetScrollY();

  const navRef = useRef(null);
  const homeRef = useRef(null);
  const expertiseRef = useRef(null);
  const certificationsRef = useRef(null);
  const projectsRef = useRef(null);
  const contactRef = useRef(null);

  const [activeLink, setActiveLink] = useState("");

  const handleLinkClick = (sectionId) => {
    const sectionElement =
      sectionId === "home"
        ? homeRef.current
        : sectionId === "expertise"
        ? expertiseRef.current
        : sectionId === "contact"
        ? contactRef.current
        : null; // Handle cases where sectionId doesn't match any known ref

    if (sectionElement) {
      setActiveLink(sectionId);
      window.scrollTo({
        top: sectionElement.offsetTop,
        behavior: "smooth",
      });
    }
  };

  const handleIsOpen = () => {
    setIsOpen(!isOpen);
    setShowMenu(!showMenu);
  };

  return (
    <Navbar ref={navRef} style={{
      background: scrollYValue > 138 ? "#40798C" : "none",
      borderRadius: scrollYValue > 138 ? "8px" : "none",
      transition: "ease-in-out 2s"
    }}>
      <Logo src={currentThemeLogo} alt="Logo" />
      {(showMenu || screenSize > 768) && (
        <Menu>
          <UList>
            <ListElement>
              <Link to="/" onClick={() => setActiveLink("home")}>Home</Link>
            </ListElement>
            <ListElement>
              <Link to="/expertise" onClick={() => setActiveLink("expertise")}>My Expertise</Link>
            </ListElement>
            <ListElement>
              <Link to="/certifications" onClick={() => setActiveLink("certifications")}>Certifications</Link>
            </ListElement>
            <ListElement>
              <Link to="/projects" onClick={() => setActiveLink("projects")}>Projects</Link>
            </ListElement>
            <ListElement>
              <Link to="/contact" onClick={() => setActiveLink("contact")}>Contact</Link>
            </ListElement>
          </UList>
          <ThemeToggle onClick={changeTheme} src={ThemeLogo} alt="Theme" />
        </Menu>
      )}
      <HamburgerMenu onClick={handleIsOpen} src={isOpen ? CloseWindow : DarkMenu} alt="Dark Menu" />
    </Navbar>
  );
}

export default Nav;
