import { Navbar, Logo, ThemeToggle, UList, ListElement, Menu, HamburgerMenu } from "./NavStyles";
import ThemeLogo from "./themes.png";
import DarkMenu from "./darkMenu.png";
import CloseWindow from "./closeWindow.png";
import { useState, useRef } from "react";
import useGetWindowSize from "./useGetWindoSize";
import useGetScrollY from "./useGetScrollY";

function Nav({ changeTheme, currentThemeLogo }) {
    const [isOpen, setIsOpen] = useState(false);
    const [showMenu, setShowMenu] = useState(false);
    const screenSize = useGetWindowSize();
    const scrollYValue = useGetScrollY();


    const homeRef = useRef(null);
    const expertiseRef = useRef(null);
    const certificationsRef = useRef(null);
    const projectsRef = useRef(null);
    const contactRef = useRef(null);

    const handleScrollTo = () => {
        if(homeRef.current && homeRef.current.id === "home") {
            window.scrollTo({
                top: homeRef.current.offsetTop,
                behavior: "smooth",
            })
        }
        if(expertiseRef.current && expertiseRef.current.id === "expertise") {
            window.scrollTo({
                top: expertiseRef.current.offsetTop,
                behavior: "smooth",
            })
        }
        if(certificationsRef.current && certificationsRef.current.id === "certifications") {

            window.scrollTo({
                top: certificationsRef.current.offsetTop,
                behavior: "smooth",
            })
        }
        if(projectsRef.current && projectsRef.current.id === "projects") {
            console.log(projectsRef.current.offsetTop);
            window.scrollTo({
                top: projectsRef.current.offsetTop,
                behavior: "smooth",
            })
        }
        if(contactRef.current && contactRef.current.id === "contacts") {
            window.scrollTo({
                top: contactRef.current.offsetTop,
                behavior: "smooth",
            })
        }
    };

    const handleIsOpen = () => {
        setIsOpen(!isOpen);
        setShowMenu(!showMenu);
    }


    return (
        <Navbar style={{
            background: scrollYValue > 138 ? "#40798C" : "none",
            borderRadius: scrollYValue > 138 ? "8px" : "none",
            transition: "ease-in-out 2s"
        }}>

            <Logo src={currentThemeLogo} alt="Logo" />
            {(showMenu || screenSize > 768) && <Menu>
                <UList>
                    <ListElement ref={homeRef} onClick={handleScrollTo} id="home">Home</ListElement>
                    <ListElement ref={expertiseRef} onClick={handleScrollTo} id="expertise">My Expertise</ListElement>
                    <ListElement ref={certificationsRef} onClick={handleScrollTo} id="certifications">Certifications</ListElement>
                    <ListElement ref={projectsRef} onClick={handleScrollTo} id="projects">Projects</ListElement>
                    <ListElement ref={contactRef} onClick={handleScrollTo} id="contacts">Contact</ListElement>
                </UList>
                <ThemeToggle onClick={changeTheme} src={ThemeLogo} alt="Theme" />
            </Menu>}
            <HamburgerMenu onClick={handleIsOpen} src={isOpen ? CloseWindow : DarkMenu} alt="Dark Menu" />
        </Navbar>
    )
}

export default Nav;
