import styled from "styled-components";


export const Navbar = styled.nav`
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;

    gap: 200px;

    @media(width <= 768px) {
        justify-content: space-between;
    }

    position: fixed;
    top: 0;
    left: 0;

    width: 100%;

    padding: 15px;

    background: #40798C;

    box-shadow: 1px 2px 5px rgba(0,0,0,0.1);
`;

export const UList = styled.ul`
    display: flex;
    align-items: center;
    justify-content: center;

    gap: 10px;

    list-style: none;

    color: ${({ theme }) => theme.fontColor};

    @media(width <= 768px) {
        flex-direction: column;
        align-items: flex-start;

        position: absolute;
        bottom: 250px;
        left: 0;

        & div {
            width: 100vw;
            height: 1px;

            background-color: ${({ theme }) => theme.fontColor};

            margin-left: -38px;
            margin-top: 5px;
            margin-bottom: 5px;
        }
    }

`;

export const ListElement = styled.li`


transition: ease-in-out 250ms;

  cursor: pointer;

  &:hover {
    color: #D4AF37;
  }
`;

export const Logo = styled.img`
width: 90px;
height: 50px;

cursor: pointer;
`;

export const ThemeToggle = styled.img`
  width: 50px;
  height: 50px;

  transition: ease-in-out 250ms;

  filter: brightness(0) invert(1);

  cursor: pointer;

  @media(width <= 768px) {
    position: absolute;
        bottom: 220px;
        left: 40px;


        width: 35px;
        height: 35px;
  }

  &:hover {
    filter: brightness(1) invert(0);
  }

`;

export const Menu = styled.div`
    display: flex;
    gap: 200px;

    @media(width <= 768px) {
        position: absolute;
        bottom: 0;
        left: 0;

        transform: translate(0,100%);
        width: 100vw;
        height: 100vh;
    }
`;

export const HamburgerMenu = styled.div`

    display: flex;
    flex-direction: column;
    justify-content: center;
    gap: 5px;

    width: 30px;
    height: 50px;

    & div {
    width: 100%;
    height: 4px;
    border-radius: 10px;

    background: ${({ theme }) => theme.fontColor};

    margin-bottom: 5px;
}

@media(width > 768px) {
    display: none;
}
`;
