import { Navbar, Logo, ThemeToggle, UList, ListElement, Menu, HamburgerMenu } from "./NavStyles";
import ThemeLogo from "./themes.png";
import DarkMenu from "./darkMenu.png";
import CloseWindow from "./closeWindow.png";
import { useState, useRef } from "react";
import { scroller } from "react-scroll";
import useGetWindowSize from "./useGetWindoSize";
import useGetScrollY from "./useGetScrollY";

function Nav({ changeTheme, currentThemeLogo }) {
    const [isOpen, setIsOpen] = useState(false);
    const [showMenu, setShowMenu] = useState(false);
    const screenSize = useGetWindowSize();
    const scrollYValue = useGetScrollY();


    const homeRef = useRef();
    const expertiseRef = useRef();
    const certificationsRef = useRef();
    const projectsRef = useRef();
    const contactRef = useRef();

    const handleScrollTo = (sectionId) => {
        const section = document.getElementById(sectionId);
        if (section) {
          scroller.scrollTo(section, { smooth: true, offset: section.offsetTop }); // Adjust offset as needed
        } else {
          console.warn(`Section with ID "${sectionId}" not found`);
        }
      };


    const handleIsOpen = () => {
        setIsOpen(!isOpen);
        setShowMenu(!showMenu);
    }


    return (
        <Navbar style={{
            background: scrollYValue > 138 ? "#40798C" : "none",
            borderRadius: scrollYValue > 138 ? "8px" : "none",
            transition: "ease-in-out 2s"
        }}>

            <Logo src={currentThemeLogo} alt="Logo" />
            {(showMenu || screenSize > 768) && <Menu>
                <UList>
                    <ListElement ref={homeRef} onClick={handleScrollTo("home")} id="home">Home</ListElement>
                    <ListElement ref={expertiseRef} onClick={handleScrollTo("expertise")} id="expertise">My Expertise</ListElement>
                    <ListElement ref={certificationsRef} onClick={handleScrollTo("certifications")} id="certifications">Certifications</ListElement>
                    <ListElement ref={projectsRef} onClick={handleScrollTo("projects")} id="projects">Projects</ListElement>
                    <ListElement ref={contactRef} onClick={() => handleScrollTo('contacts')} id="contacts">Contact</ListElement>
                </UList>
                <ThemeToggle onClick={changeTheme} src={ThemeLogo} alt="Theme" />
            </Menu>}
            <HamburgerMenu onClick={handleIsOpen} src={isOpen ? CloseWindow : DarkMenu} alt="Dark Menu" />
        </Navbar>
    )
}

export default Nav;
